mapper.select:
select travel_team_id,travel_team_title,travel_team_keyword,travel_team_type,travel_team_shoper,travel_team_line,travel_team_price,travel_team_beginTime,travel_team_endTime,travel_team_origin,travel_team_distance,travel_team_peoples,travel_team_collects,travel_team_img from travel_team
travel_team_title LIKE CONCAT(CONCAT('%',#{search}),'%')
limit #{offset},#{pageSize}
